import React, { useState } from "react";
import styled from "styled-components";

const Modal = styled.div`
  display: ${(props) => (props.show ? "block" : "none")};
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background-color: #474e5d;
  padding-top: 50px;
`;

const ModalContent = styled.form`
  background-color: #fefefe;
  margin: 5% auto 15% auto;
  border: 1px solid #888;
  width: 80%;
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
`;

const Input = styled.input`
  width: 100%;
  padding: 15px;
  margin: 5px 0 22px 0;
  border: none;
  background: #f1f1f1;

  &:focus {
    background-color: #ddd;
    outline: none;
  }
`;

const Button = styled.button`
  background-color: ${(props) => (props.cancel ? "#f44336" : "#04AA6D")};
  color: white;
  padding: 14px 20px;
  margin: 8px 0;
  border: none;
  cursor: pointer;
  width: 100%;
  opacity: 0.9;

  &:hover {
    opacity: 1;
  }
`;

const Close = styled.span`
  position: absolute;
  right: 35px;
  top: 15px;
  font-size: 40px;
  font-weight: bold;
  color: #f1f1f1;

  &:hover,
  &:focus {
    color: #f44336;
    cursor: pointer;
  }
`;

const Clearfix = styled.div`
  &::after {
    content: "";
    clear: both;
    display: table;
  }
`;

const HR = styled.hr`
  border: 1px solid #f1f1f1;
  margin-bottom: 25px;
`;

const SignupForm = () => {
    const [isModalOpen, setIsModalOpen] = useState(true);

    const handleOutsideClick = (event) => {
        if (event.target.id === "modal") {
            setIsModalOpen(false);
        }
    };

    return (
        <div>
            <Modal id="modal" show={isModalOpen} onClick={handleOutsideClick}>
                <Close onClick={() => setIsModalOpen(false)} title="Close Modal">
                    &times;
                </Close>
                <ModalContent>
                    <Container>
                        <h1>Sign Up</h1>
                        <p>Please fill in this form to create an account.</p>
                        <HR />

                        <label htmlFor="email"><b>Email</b></label>
                        <Input type="text" placeholder="Enter Email" name="email" required />

                        <label htmlFor="psw"><b>Password</b></label>
                        <Input type="password" placeholder="Enter Password" name="psw" required />

                        <label htmlFor="psw-repeat"><b>Repeat Password</b></label>
                        <Input
                            type="password"
                            placeholder="Repeat Password"
                            name="psw-repeat"
                            required
                        />

                        <label>
                            <Input type="checkbox" name="remember" defaultChecked /> Remember me
                        </label>

                        <p>
                            By creating an account you agree to our <a href="#">Terms & Privacy</a>.
                        </p>

                        <Clearfix>
                            <Button cancel onClick={() => setIsModalOpen(false)} type="button">
                                Cancel
                            </Button>
                            <Button type="submit">Sign Up</Button>
                        </Clearfix>
                    </Container>
                </ModalContent>
            </Modal>
        </div>
    );
};

export default SignupForm;
