import React, { useEffect, useState, useRef } from 'react'
import MovieItem from './MovieItem'
import Navbar from './Navbar';
import { useNavigate } from "react-router-dom";
import styled from 'styled-components';

// Styled Components for Movie Grid
const MovieGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    max-width: 1350px;
    margin: auto;
    gap: 20px;
    padding: 20px;

    @media (min-width: 900px) {
        grid-template-columns: repeat(3, 1fr); /* Max 3 columns */
    }
`;

export default function Dashboard() {
    const [username, setUsername] = useState("")
    const fetched = useRef(false) // useRef prevents fetching api multiple time
    const navigate = useNavigate()

    useEffect(() => {
        if (fetched.current) return;
        fetched.current = true

        fetch("http://localhost:3001/api/login", {
            method: "GET",
            credentials: "include", // Ensures cookies are sent
        })
            .then((response) => response.json())
            .then((data) => {
                if (!data.username) {
                    navigate('/')
                } else {
                    setUsername(data.username || "Try Logging in First");
                }
            })
            .catch((error) => console.error("Error fetching session:", error));
    }, [navigate]);

    fetchMovie = () => {
        const [items, setItems] = useState([])

        useEffect(() => {
            const url = "http://localhost:3001/api/getmovie"
            fetch(url)
                .then((response) => response.json())
                .then((data) => setItems(data))
                .catch((error) => console.error("Error fetching data:", error));
        }, [])
    }

    return (
        <>
            <Navbar />
            <h3>Hello {username}</h3>
            {items.map((item) => (
                <MovieItem key={item.Id} item={item} />
            ))}
        </>
    )
}
